// Dependencies
import React, { Fragment } from 'react';
import { Switch, Route } from 'react-router-dom';

// Styles
import './assets/css/app.scss';
import './assets/css/bootstrap.min.css';

// Components
// Ej. import Navbar from './components/Navbar';
// Test:
const About = ({ text }) => <h2>Soy la sección ABOUT: {text}</h2>;
const Contact = (props) => <h6>Llegaste a la sección CONTACT</h6>;

const App = () => {
  return (
    <Fragment>
      <Switch>
        {/* Route clasic */}
        <Route path="/" exact>
          <About text="Saludos a todos" />{' '}
        </Route>

        {/* Route withouts params */}
        <Route path="/contact" component={Contact} />

        {/* Route arrow function */}
        <Route path="/about" render={() => <About text="Saludos a todos" />} />
      </Switch>
    </Fragment>
  );
};

export default App;
